-- Reinsert Atal'ai Tablet 37099 using wotlk-db guids
DELETE FROM `gameobject` WHERE `id` = 37099 AND `map` != 109;

-- https://classic.wowhead.com/quest=1475/into-the-temple-of-atalhakkar#comments:id=63536
UPDATE `gameobject` SET `spawntimesecsmin` = 360, `spawntimesecsmax` = 480, `animprogress` = 100, `state` = 1 WHERE `id` = 37099 AND `map` != 109;

-- Bonus: Correct size for Atal'ai Light SMALL (DND) 148883
UPDATE `gameobject_template` SET `faction` = 14, `size` = 3.5 WHERE `entry` = 148883; -- Atal'ai Light SMALL (DND)

